#include <stdio.h>

int main() {
    int a = 10, b = 3;

    // Arithmetic operators
    printf("Addition: %d\n", a + b);
    printf("Subtraction: %d\n", a - b);
    printf("Multiplication: %d\n", a * b);
    printf("Division: %d\n", a / b);
    printf("Modulus: %d\n", a % b);

    // Relational operators
    printf("a > b: %d\n", a > b);  // 1 (true)
    printf("a == b: %d\n", a == b); // 0 (false)

    // Logical operators
    int x = 1, y = 0;
    printf("x && y: %d\n", x && y); // 0 (false)
    printf("x || y: %d\n", x || y); // 1 (true)
    printf("!x: %d\n", !x);         // 0 (false)

    // Bitwise operators
    int num1 = 5;  // 0101
    int num2 = 3;  // 0011

    printf("Bitwise AND: %d\n", num1 & num2);  // 1 (0001)
    printf("Bitwise OR: %d\n", num1 | num2);   // 7 (0111)
    printf("Bitwise XOR: %d\n", num1 ^ num2);  // 6 (0110)
    printf("Left shift: %d\n", num1 << 1);     // 10 (1010)
    printf("Right shift: %d\n", num1 >> 1);    // 2 (0010)

    // Assignment operators
    int c = 5;
    c += 3;  // c = c + 3
    printf("c += 3: %d\n", c);

    return 0;
}

#include <stdio.h>

int main() {
    // If-else statement
    int marks = 85;

    if (marks >= 90) {
        printf("Grade: A\n");
    } else if (marks >= 75) {
        printf("Grade: B\n");
    } else if (marks >= 50) {
        printf("Grade: C\n");
    } else {
        printf("Grade: F\n");
    }

    // Switch-case statement
    char grade = 'B';

    switch(grade) {
        case 'A':
            printf("Excellent!\n");
            break;
        case 'B':
            printf("Good!\n");
            break;
        case 'C':
            printf("Average\n");
            break;
        default:
            printf("Invalid grade\n");
    }

    // For loop
    printf("For loop: ");
    for(int i = 1; i <= 5; i++) {
        printf("%d ", i);
    }
    printf("\n");

    // While loop
    printf("While loop: ");
    int j = 1;
    while(j <= 5) {
        printf("%d ", j);
        j++;
    }
    printf("\n");

    // Do-while loop
    printf("Do-while loop: ");
    int k = 1;
    do {
        printf("%d ", k);
        k++;
    } while(k <= 5);
    printf("\n");

    // Nested loops
    printf("Multiplication Table:\n");
    for(int row = 1; row <= 5; row++) {
        for(int col = 1; col <= 5; col++) {
            printf("%d\t", row * col);
        }
        printf("\n");
    }

    return 0;
}

#include <stdio.h>

// Function prototypes (declaration)
int add(int a, int b);
void greet();
float calculateAverage(int arr[], int size);
int factorial(int n);

int main() {
    // Function calls
    greet();

    int sum = add(10, 20);
    printf("Sum: %d\n", sum);

    // Array average
    int numbers[] = {10, 20, 30, 40, 50};
    float avg = calculateAverage(numbers, 5);
    printf("Average: %.2f\n", avg);

    // Recursive function
    int fact = factorial(5);
    printf("Factorial of 5: %d\n", fact);

    return 0;
}

// Function definitions

// Simple function
void greet() {
    printf("Welcome to C Functions!\n");
}

// Function with parameters and return value
int add(int a, int b) {
    return a + b;
}

// Function with array parameter
float calculateAverage(int arr[], int size) {
    int sum = 0;
    for(int i = 0; i < size; i++) {
        sum += arr[i];
    }
    return (float)sum / size;
}

// Recursive function
int factorial(int n) {
    if (n == 0 || n == 1) {
        return 1;
    }
    return n * factorial(n - 1);
}

// Function with call by value
void swapByValue(int a, int b) {
    int temp = a;
    a = b;
    b = temp;
    printf("Inside swapByValue: a=%d, b=%d\n", a, b);
}

// Function with call by reference
void swapByReference(int *a, int *b) {
    int temp = *a;
    *a = *b;
    *b = temp;
}

#include <stdio.h>

int main() {
    // 1D Array
    int numbers[5] = {10, 20, 30, 40, 50};

    printf("1D Array:\n");
    for(int i = 0; i < 5; i++) {
        printf("numbers[%d] = %d\n", i, numbers[i]);
    }

    // Array of characters (String)
    char name[] = "C Programming";
    printf("\nString: %s\n", name);
    printf("First character: %c\n", name[0]);

    // 2D Array (Matrix)
    int matrix[3][3] = {
        {1, 2, 3},
        {4, 5, 6},
        {7, 8, 9}
    };

    printf("\n2D Array (Matrix):\n");
    for(int i = 0; i < 3; i++) {
        for(int j = 0; j < 3; j++) {
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }

    // Array operations
    int arr[10];
    int size = 5;

    // Insertion
    for(int i = 0; i < size; i++) {
        arr[i] = (i + 1) * 10;
    }

    // Deletion (remove element at index 2)
    for(int i = 2; i < size - 1; i++) {
        arr[i] = arr[i + 1];
    }
    size--;

    // Searching
    int search = 40;
    int found = -1;
    for(int i = 0; i < size; i++) {
        if(arr[i] == search) {
            found = i;
            break;
        }
    }

    if(found != -1) {
        printf("\nElement %d found at index %d\n", search, found);
    } else {
        printf("\nElement not found\n");
    }

    return 0;
}

#include <stdio.h>

int main() {
    int num = 42;
    int *ptr;  // Pointer declaration

    ptr = &num; // Assign address of num to ptr

    printf("Value of num: %d\n", num);
    printf("Address of num: %p\n", &num);
    printf("Value of ptr: %p\n", ptr);
    printf("Value at ptr: %d\n", *ptr); // Dereferencing

    // Pointer arithmetic
    int arr[] = {10, 20, 30, 40, 50};
    int *arrPtr = arr; // Points to first element

    printf("\nArray elements using pointers:\n");
    for(int i = 0; i < 5; i++) {
        printf("arr[%d] = %d\n", i, *(arrPtr + i));
        // *(arrPtr + i) is same as arr[i]
    }

    // Pointer to pointer
    int x = 100;
    int *p1 = &x;
    int **p2 = &p1;

    printf("\nPointer to pointer:\n");
    printf("x = %d\n", x);
    printf("*p1 = %d\n", *p1);
    printf("**p2 = %d\n", **p2);

    // Void pointer (generic pointer)
    void *voidPtr;
    float f = 3.14;
    voidPtr = &f;

    printf("\nVoid pointer:\n");
    printf("Float value: %.2f\n", *(float*)voidPtr); // Type casting needed

    // NULL pointer
    int *nullPtr = NULL;
    if(nullPtr == NULL) {
        printf("\nPointer is NULL\n");
    }

    // Pointer comparison
    int *ptr1 = &arr[0];
    int *ptr2 = &arr[2];

    if(ptr1 < ptr2) {
        printf("ptr1 points to lower memory address\n");
    }

    return 0;
}

#include <stdio.h>
#include <string.h>

int main() {
    // String declaration methods
    char str1[] = "Hello";  // Automatic null termination
    char str2[10] = {'W', 'o', 'r', 'l', 'd', '\0'};
    char str3[50];

    printf("str1: %s\n", str1);
    printf("str2: %s\n", str2);

    // String input
    printf("Enter your name: ");
    fgets(str3, sizeof(str3), stdin);  // Safe input
    printf("Name: %s", str3);

    // String functions
    char source[] = "C Programming";
    char destination[50];

    // strlen() - string length
    printf("Length of '%s': %zu\n", source, strlen(source));

    // strcpy() - string copy
    strcpy(destination, source);
    printf("Copied string: %s\n", destination);

    // strcat() - string concatenation
    char greet[] = "Hello ";
    char name[] = "Alice";
    strcat(greet, name);
    printf("Concatenated: %s\n", greet);

    // strcmp() - string comparison
    char pass1[] = "password123";
    char pass2[] = "password123";

    if(strcmp(pass1, pass2) == 0) {
        printf("Passwords match\n");
    } else {
        printf("Passwords don't match\n");
    }

    // strstr() - find substring
    char text[] = "Learning C programming is fun";
    char word[] = "C";

    char *result = strstr(text, word);
    if(result != NULL) {
        printf("'%s' found in text\n", word);
    }

    // Manual string operations
    char manual[50] = "Hello";
    int length = 0;

    // Calculate length manually
    while(manual[length] != '\0') {
        length++;
    }
    printf("Manual length: %d\n", length);

    // Reverse string
    char rev[] = "ABCDE";
    int len = strlen(rev);

    printf("Original: %s\n", rev);
    printf("Reversed: ");
    for(int i = len - 1; i >= 0; i--) {
        printf("%c", rev[i]);
    }
    printf("\n");

    return 0;
}

#include <stdio.h>
#include <string.h>

// Structure definition
struct Student {
    int rollNo;
    char name[50];
    float marks;
    char grade;
};

// Structure with typedef
typedef struct {
    int id;
    char title[100];
    char author[50];
    float price;
} Book;

// Nested structure
struct Address {
    char city[50];
    char state[50];
    int pincode;
};

struct Person {
    char name[50];
    int age;
    struct Address addr;  // Nested structure
};

// Union definition
union Data {
    int i;
    float f;
    char str[20];
};

int main() {
    // Structure variable declaration
    struct Student s1;

    // Assigning values
    s1.rollNo = 101;
    strcpy(s1.name, "Rahul Kumar");
    s1.marks = 85.5;
    s1.grade = 'A';

    printf("Student Details:\n");
    printf("Roll No: %d\n", s1.rollNo);
    printf("Name: %s\n", s1.name);
    printf("Marks: %.2f\n", s1.marks);
    printf("Grade: %c\n", s1.grade);

    // Using typedef
    Book b1 = {1, "C Programming", "Dennis Ritchie", 499.99};
    printf("\nBook: %s by %s\n", b1.title, b1.author);

    // Array of structures
    struct Student class[3] = {
        {101, "Alice", 90.0, 'A'},
        {102, "Bob", 75.5, 'B'},
        {103, "Charlie", 60.0, 'C'}
    };

    printf("\nClass Details:\n");
    for(int i = 0; i < 3; i++) {
        printf("%s: %.1f marks\n", class[i].name, class[i].marks);
    }

    // Pointer to structure
    struct Student *ptr = &s1;
    printf("\nUsing pointer: %s\n", ptr->name);  // Arrow operator

    // Nested structure
    struct Person p1 = {
        "John Doe",
        30,
        {"Mumbai", "Maharashtra", 400001}
    };

    printf("\nPerson lives in: %s, %s\n", p1.addr.city, p1.addr.state);

    // Union example
    union Data data;

    data.i = 10;
    printf("\nUnion integer value: %d\n", data.i);

    data.f = 3.14;
    printf("Union float value: %.2f\n", data.f);
    // data.i is now corrupted

    printf("Size of union: %zu bytes\n", sizeof(union Data));
    printf("Size of struct Student: %zu bytes\n", sizeof(struct Student));

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    // malloc() - Memory Allocation
    int *arr;
    int n;

    printf("Enter array size: ");
    scanf("%d", &n);

    // Allocate memory for n integers
    arr = (int*)malloc(n * sizeof(int));

    if(arr == NULL) {
        printf("Memory allocation failed!\n");
        return 1;
    }

    printf("Enter %d integers:\n", n);
    for(int i = 0; i < n; i++) {
        scanf("%d", &arr[i]);
    }

    printf("Array elements: ");
    for(int i = 0; i < n; i++) {
        printf("%d ", arr[i]);
    }
    printf("\n");

    // Free allocated memory
    free(arr);

    // calloc() - Contiguous Allocation
    int *arr2;
    printf("\nEnter new array size: ");
    scanf("%d", &n);

    arr2 = (int*)calloc(n, sizeof(int));

    if(arr2 == NULL) {
        printf("Memory allocation failed!\n");
        return 1;
    }

    // calloc initializes to zero
    printf("calloc array (initialized to 0): ");
    for(int i = 0; i < n; i++) {
        printf("%d ", arr2[i]);
    }
    printf("\n");

    // realloc() - Reallocate memory
    printf("\nEnter new size for reallocation: ");
    scanf("%d", &n);

    arr2 = (int*)realloc(arr2, n * sizeof(int));

    if(arr2 == NULL) {
        printf("Reallocation failed!\n");
        return 1;
    }

    printf("Enter %d new integers:\n", n);
    for(int i = 0; i < n; i++) {
        scanf("%d", &arr2[i]);
    }

    printf("Reallocated array: ");
    for(int i = 0; i < n; i++) {
        printf("%d ", arr2[i]);
    }
    printf("\n");

    // Dynamic 2D array
    int rows, cols;
    printf("\nEnter rows and columns for 2D array: ");
    scanf("%d %d", &rows, &cols);

    int **matrix = (int**)malloc(rows * sizeof(int*));
    for(int i = 0; i < rows; i++) {
        matrix[i] = (int*)malloc(cols * sizeof(int));
    }

    // Initialize 2D array
    int value = 1;
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            matrix[i][j] = value++;
        }
    }

    printf("\n2D Dynamic Array:\n");
    for(int i = 0; i < rows; i++) {
        for(int j = 0; j < cols; j++) {
            printf("%d\t", matrix[i][j]);
        }
        printf("\n");
    }

    // Free 2D array memory
    for(int i = 0; i < rows; i++) {
        free(matrix[i]);
    }
    free(matrix);

    // Free remaining memory
    free(arr2);

    // Set pointers to NULL after freeing
    arr = NULL;
    arr2 = NULL;
    matrix = NULL;

    printf("\nAll memory freed successfully!\n");

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    FILE *filePtr;
    char data[100];

    // Writing to a text file
    filePtr = fopen("example.txt", "w");

    if(filePtr == NULL) {
        printf("Error opening file for writing!\n");
        return 1;
    }

    printf("Enter text to write to file: ");
    fgets(data, sizeof(data), stdin);

    fprintf(filePtr, "%s", data);
    printf("Data written to file successfully!\n");

    fclose(filePtr);

    // Reading from a text file
    filePtr = fopen("example.txt", "r");

    if(filePtr == NULL) {
        printf("Error opening file for reading!\n");
        return 1;
    }

    printf("\nReading from file:\n");
    while(fgets(data, sizeof(data), filePtr) != NULL) {
        printf("%s", data);
    }

    fclose(filePtr);

    // Appending to a file
    filePtr = fopen("example.txt", "a");

    if(filePtr == NULL) {
        printf("Error opening file for appending!\n");
        return 1;
    }

    printf("\nEnter text to append: ");
    fgets(data, sizeof(data), stdin);

    fprintf(filePtr, "%s", data);
    printf("Data appended successfully!\n");

    fclose(filePtr);

    // Binary file operations
    struct Student {
        int rollNo;
        char name[50];
        float marks;
    };

    struct Student s;

    // Write to binary file
    filePtr = fopen("students.dat", "wb");

    if(filePtr == NULL) {
        printf("Error creating binary file!\n");
        return 1;
    }

    for(int i = 1; i <= 3; i++) {
        s.rollNo = i;
        sprintf(s.name, "Student %d", i);
        s.marks = 70.0 + i * 5;

        fwrite(&s, sizeof(struct Student), 1, filePtr);
    }

    fclose(filePtr);

    // Read from binary file
    filePtr = fopen("students.dat", "rb");

    if(filePtr == NULL) {
        printf("Error opening binary file!\n");
        return 1;
    }

    printf("\nStudents from binary file:\n");
    printf("Roll No\tName\t\tMarks\n");
    printf("------------------------\n");

    while(fread(&s, sizeof(struct Student), 1, filePtr)) {
        printf("%d\t%s\t%.1f\n", s.rollNo, s.name, s.marks);
    }

    fclose(filePtr);

    // File error handling
    filePtr = fopen("nonexistent.txt", "r");

    if(filePtr == NULL) {
        perror("Error opening file");
        printf("Error code: %d\n", errno);
    }

    // File position operations
    filePtr = fopen("example.txt", "r");

    if(filePtr != NULL) {
        // Move to 10th byte from beginning
        fseek(filePtr, 10, SEEK_SET);

        // Get current position
        long position = ftell(filePtr);
        printf("\nCurrent file position: %ld\n", position);

        // Move to beginning
        rewind(filePtr);

        fclose(filePtr);
    }

    // Delete file
    if(remove("example.txt") == 0) {
        printf("File deleted successfully!\n");
    } else {
        printf("Error deleting file!\n");
    }

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

// Linked List Node
struct Node {
    int data;
    struct Node* next;
};

// Stack using array
#define MAX 100

struct Stack {
    int arr[MAX];
    int top;
};

// Queue using array
struct Queue {
    int arr[MAX];
    int front, rear;
};

// Linked List operations
void insertNode(struct Node** head, int data) {
    struct Node* newNode = (struct Node*)malloc(sizeof(struct Node));
    newNode->data = data;
    newNode->next = *head;
    *head = newNode;
}

void displayList(struct Node* head) {
    printf("Linked List: ");
    while(head != NULL) {
        printf("%d -> ", head->data);
        head = head->next;
    }
    printf("NULL\n");
}

// Stack operations
void initStack(struct Stack* s) {
    s->top = -1;
}

int isStackEmpty(struct Stack* s) {
    return s->top == -1;
}

int isStackFull(struct Stack* s) {
    return s->top == MAX - 1;
}

void push(struct Stack* s, int data) {
    if(isStackFull(s)) {
        printf("Stack Overflow!\n");
        return;
    }
    s->arr[++(s->top)] = data;
}

int pop(struct Stack* s) {
    if(isStackEmpty(s)) {
        printf("Stack Underflow!\n");
        return -1;
    }
    return s->arr[(s->top)--];
}

// Queue operations
void initQueue(struct Queue* q) {
    q->front = q->rear = -1;
}

int isQueueEmpty(struct Queue* q) {
    return q->front == -1;
}

int isQueueFull(struct Queue* q) {
    return (q->rear + 1) % MAX == q->front;
}

void enqueue(struct Queue* q, int data) {
    if(isQueueFull(q)) {
        printf("Queue Overflow!\n");
        return;
    }

    if(isQueueEmpty(q)) {
        q->front = q->rear = 0;
    } else {
        q->rear = (q->rear + 1) % MAX;
    }

    q->arr[q->rear] = data;
}

int dequeue(struct Queue* q) {
    if(isQueueEmpty(q)) {
        printf("Queue Underflow!\n");
        return -1;
    }

    int data = q->arr[q->front];

    if(q->front == q->rear) {
        q->front = q->rear = -1;
    } else {
        q->front = (q->front + 1) % MAX;
    }

    return data;
}

int main() {
    printf("=== Data Structures in C ===\n\n");

    // Linked List Demo
    printf("1. Linked List Demo:\n");
    struct Node* head = NULL;

    insertNode(&head, 10);
    insertNode(&head, 20);
    insertNode(&head, 30);

    displayList(head);

    // Stack Demo
    printf("\n2. Stack Demo:\n");
    struct Stack s;
    initStack(&s);

    push(&s, 100);
    push(&s, 200);
    push(&s, 300);

    printf("Popped: %d\n", pop(&s));
    printf("Popped: %d\n", pop(&s));

    // Queue Demo
    printf("\n3. Queue Demo:\n");
    struct Queue q;
    initQueue(&q);

    enqueue(&q, 1000);
    enqueue(&q, 2000);
    enqueue(&q, 3000);

    printf("Dequeued: %d\n", dequeue(&q));
    printf("Dequeued: %d\n", dequeue(&q));

    // Binary Tree (Basic)
    printf("\n4. Binary Tree Concept:\n");
    printf("struct TreeNode {\n");
    printf("    int data;\n");
    printf("    struct TreeNode* left;\n");
    printf("    struct TreeNode* right;\n");
    printf("};\n");

    // Memory cleanup for linked list
    struct Node* temp;
    while(head != NULL) {
        temp = head;
        head = head->next;
        free(temp);
    }

    printf("\nAll data structures demonstrated!\n");

    return 0;
}
