detailed_explanation.example_code:
#include <iostream>
using namespace std;

int main() {
    int a = 10, b = 3;

    // 1. ARITHMETIC OPERATORS
    cout << "Arithmetic Operators:\n";
    cout << "a + b = " << (a + b) << endl;  // Addition
    cout << "a - b = " << (a - b) << endl;  // Subtraction
    cout << "a * b = " << (a * b) << endl;  // Multiplication
    cout << "a / b = " << (a / b) << endl;  // Division (integer)
    cout << "a % b = " << (a % b) << endl;  // Modulus (remainder)

    // 2. RELATIONAL OPERATORS (return true/false)
    cout << "\nRelational Operators:\n";
    cout << "a > b: " << (a > b) << endl;   // Greater than
    cout << "a < b: " << (a < b) << endl;   // Less than
    cout << "a == b: " << (a == b) << endl; // Equal to
    cout << "a != b: " << (a != b) << endl; // Not equal
    cout << "a >= b: " << (a >= b) << endl; // Greater or equal
    cout << "a <= b: " << (a <= b) << endl; // Less or equal

    // 3. LOGICAL OPERATORS
    bool x = true, y = false;
    cout << "\nLogical Operators:\n";
    cout << "x && y: " << (x && y) << endl; // AND (both true)
    cout << "x || y: " << (x || y) << endl; // OR (at least one true)
    cout << "!x: " << (!x) << endl;         // NOT (reverse)

    // 4. ASSIGNMENT OPERATORS
    cout << "\nAssignment Operators:\n";
    int c = 5;
    c += 3;  // c = c + 3
    cout << "c += 3: " << c << endl;

    c -= 2;  // c = c - 2
    cout << "c -= 2: " << c << endl;

    c *= 2;  // c = c * 2
    cout << "c *= 2: " << c << endl;

    // 5. INCREMENT/DECREMENT
    cout << "\nIncrement/Decrement:\n";
    int d = 5;
    cout << "d++: " << d++ << endl;  // Post-increment (use then increment)
    cout << "d: " << d << endl;
    cout << "++d: " << ++d << endl;  // Pre-increment (increment then use)

    return 0;
}

