    else if (marks >= 75) {
        grade = 'B';  // 75-89: B grade
    }
    else if (marks >= 50) {
        grade = 'C';  // 50-74: C grade
    }
    else {
        grade = 'F';  // Below 50: Fail
    }

    // Display result
    cout << "Grade: " << grade << endl;

    return 0;  // Program ends successfully
}

