#include <iostream>
using namespace std;

int main() {
    int marks;

    cout << "Enter marks (0-100): ";
    cin >> marks;

    // 1. Simple if statement
    cout << "\n1. Simple if:\n";
    if (marks >= 33) {
        cout << "Pass!\n";
    }

    // 2. if-else statement
    cout << "\n2. if-else:\n";
    if (marks >= 33) {
        cout << "Pass\n";
    } else {
        cout << "Fail\n";
    }

    // 3. if-else if ladder
    cout << "\n3. if-else if ladder:\n";
    if (marks >= 90) {
        cout << "Grade: A\n";
    }
    else if (marks >= 75) {
        cout << "Grade: B\n";
    }
    else if (marks >= 50) {
        cout << "Grade: C\n";
    }
    else if (marks >= 33) {
        cout << "Grade: D\n";
    }
    else {
        cout << "Grade: F (Fail)\n";
    }

    // 4. Nested if statements
    cout << "\n4. Nested if (Scholarship check):\n";
    if (marks >= 60) {
        cout << "Passed with good marks.\n";

        if (marks >= 90) {
            cout << "Eligible for full scholarship!\n";
        }
        else if (marks >= 75) {
            cout << "Eligible for partial scholarship.\n";
        }
    }

    // 5. One-line if (without braces)
    cout << "\n5. One-line if:\n";
    if (marks == 100)
        cout << "Perfect score!\n";

    // 6. Ternary operator (shorthand if-else)
    cout << "\n6. Ternary operator:\n";
    string result = (marks >= 33) ? "Pass" : "Fail";
    cout << "Result: " << result << endl;

    return 0;
}

