            break;

        case 3:
            cout << "Wednesday";
            break;

        case 4:
            cout << "Thursday";
            break;

        case 5:
            cout << "Friday";
            break;

        case 6:
            cout << "Saturday";
            break;

        case 7:
            cout << "Sunday";
            break;

        default:
            cout << "Invalid day number!";
    }

    cout << "\n\n--- More Examples ---\n";

    // Example 2: Calculator
    char operatorSymbol = '+';
    int a = 10, b = 5;

    switch(operatorSymbol) {
        case '+':
            cout << a << " + " << b << " = " << (a + b) << endl;
            break;

        case '-':
            cout << a << " - " << b << " = " << (a - b) << endl;
            break;

        case '*':
            cout << a << " * " << b << " = " << (a * b) << endl;
            break;

        case '/':
            cout << a << " / " << b << " = " << (a / b) << endl;
            break;

        default:
            cout << "Invalid operator" << endl;
    }

    // Example 3: Multiple cases for same action
    char grade = 'B';
    cout << "\nGrade Analysis: ";

    switch(grade) {
        case 'A':
        case 'B':
            cout << "Well done!";
            break;

        case 'C':
        case 'D':
            cout << "You passed";
            break;

        case 'F':
            cout << "Better try again";
            break;

        default:
            cout << "Invalid grade";
    }

    // Example 4: switch with enum
    enum Color {RED, GREEN, BLUE};
    Color color = GREEN;

    cout << "\n\nColor: ";
    switch(color) {
        case RED: cout << "Red"; break;
        case GREEN: cout << "Green"; break;
        case BLUE: cout << "Blue"; break;
    }

    return 0;
}

