#include <iostream>
using namespace std;

int main() {
    cout << "=== FOR LOOP EXAMPLES ===\n\n";

    // 1. Basic for loop
    cout << "1. Counting 1 to 5:\n";
    for(int i = 1; i <= 5; i++) {
        cout << i << " ";
    }
    cout << "\n\n";

    // 2. Reverse counting
    cout << "2. Reverse counting 5 to 1:\n";
    for(int i = 5; i >= 1; i--) {
        cout << i << " ";
    }
    cout << "\n\n";

    // 3. Step by 2
    cout << "3. Even numbers 0 to 10:\n";
    for(int i = 0; i <= 10; i += 2) {
        cout << i << " ";
    }
    cout << "\n\n";

    cout << "=== WHILE LOOP EXAMPLES ===\n\n";

    // 4. Basic while loop
    cout << "4. While loop (1 to 5):\n";
    int count = 1;
    while(count <= 5) {
        cout << count << " ";
        count++;
    }
    cout << "\n\n";

    // 5. User-controlled while loop
    cout << "5. User input until negative:\n";
    int number;
    int sum = 0;

    cout << "Enter numbers (negative to stop):\n";
    cin >> number;

    while(number >= 0) {
        sum += number;
        cin >> number;
    }
    cout << "Sum: " << sum << "\n\n";

    cout << "=== DO-WHILE LOOP EXAMPLES ===\n\n";

    // 6. Basic do-while (executes at least once)
    cout << "6. Do-while (menu example):\n";
    char choice;

    do {
        cout << "\nMenu:\n";
        cout << "1. Say Hello\n";
        cout << "2. Say Goodbye\n";
        cout << "3. Exit\n";
        cout << "Enter choice: ";
        cin >> choice;

        switch(choice) {
            case '1': cout << "Hello!\n"; break;
            case '2': cout << "Goodbye!\n"; break;
            case '3': cout << "Exiting...\n"; break;
            default: cout << "Invalid choice!\n";
        }

    } while(choice != '3');

    // 7. Nested loops (pattern printing)
    cout << "\n7. Nested loops (pattern):\n";
    for(int i = 1; i <= 5; i++) {
        for(int j = 1; j <= i; j++) {
            cout << "* ";
        }
        cout << endl;
    }

    // 8. Infinite loop with break
    cout << "\n8. Infinite loop with break:\n";
    int counter = 0;
    while(true) {  // Infinite loop
        counter++;
        if(counter > 5) {
            break;  // Exit loop
        }
        cout << counter << " ";
    }

    return 0;
}

