#include <iostream>
using namespace std;

int main() {
    cout << "=== BREAK STATEMENT ===\n\n";

    // 1. break in for loop
    cout << "1. Breaking loop at 5:\n";
    for(int i = 1; i <= 10; i++) {
        if(i == 5) {
            break;  // Exit loop when i becomes 5
        }
        cout << i << " ";
    }
    cout << "\n(Loop stopped at 5)\n\n";

    // 2. break in while loop
    cout << "2. Breaking while loop:\n";
    int num = 1;
    while(true) {  // Infinite loop
        cout << num << " ";
        if(num == 3) {
            break;  // Exit infinite loop
        }
        num++;
    }
    cout << "\n\n";

    // 3. break in nested loops
    cout << "3. Breaking nested loops:\n";
    for(int i = 1; i <= 3; i++) {
        for(int j = 1; j <= 3; j++) {
            if(i == 2 && j == 2) {
                cout << "\nBreaking at i=" << i << ", j=" << j;
                break;  // Only breaks inner loop
            }
            cout << "(" << i << "," << j << ") ";
        }
    }
    cout << "\n\n";

    cout << "=== CONTINUE STATEMENT ===\n\n";

    // 4. continue in for loop
    cout << "4. Skipping even numbers:\n";
    for(int i = 1; i <= 10; i++) {
        if(i % 2 == 0) {
            continue;  // Skip even numbers
        }
        cout << i << " ";  // Only odd numbers printed
    }
    cout << "\n\n";

    // 5. continue in while loop
    cout << "5. Skipping multiples of 3:\n";
    int counter = 0;
    while(counter < 10) {
        counter++;
        if(counter % 3 == 0) {
            continue;  // Skip multiples of 3
        }
        cout << counter << " ";
    }
    cout << "\n\n";

    cout << "=== GOTO STATEMENT ===\n\n";

    // 6. goto statement (use sparingly!)
    cout << "6. Goto example (emergency exit):\n";
    int value;

    for(int i = 0; i < 3; i++) {
        cout << "Enter a positive number: ";
        cin >> value;

        if(value < 0) {
            cout << "Negative number! Going to error handler.\n";
            goto error_handler;  // Jump to label
        }
        cout << "Good number: " << value << "\n";
    }

    goto end_program;  // Skip error handler if no error

    // Label definition
    error_handler:
    cout << "\nERROR: Negative number entered!\n";
    cout << "Program terminated.\n";
    return 1;  // Exit with error code

    end_program:
    cout << "\nProgram completed successfully.\n";

    // 7. Practical use of break (searching)
    cout << "\n7. Practical break (search in array):\n";
    int numbers[] = {10, 20, 30, 40, 50};
    int search = 30;
    bool found = false;

    for(int i = 0; i < 5; i++) {
        if(numbers[i] == search) {
            cout << "Found " << search << " at position " << i << endl;
            found = true;
            break;  // Stop searching once found
        }
    }

    if(!found) {
        cout << search << " not found in array.\n";
    }

    return 0;
}

