#include <iostream>
using namespace std;

// 1. FUNCTION DECLARATIONS (Prototypes)
// These tell compiler about functions before they're defined
int add(int a, int b);               // Takes 2 ints, returns int
void greet(string name);             // Takes string, returns nothing
float calculateAverage(int x, int y); // Takes 2 ints, returns float
bool isEven(int number);             // Takes int, returns bool

int main() {
    cout << "=== FUNCTION BASICS ===\n\n";

    // Using functions
    int sum = add(5, 3);
    cout << "5 + 3 = " << sum << endl;

    greet("Rahul");

    float avg = calculateAverage(10, 20);
    cout << "Average of 10 and 20: " << avg << endl;

    if(isEven(7)) {
        cout << "7 is even\n";
    } else {
        cout << "7 is odd\n";
    }

    return 0;
}

// 2. FUNCTION DEFINITIONS
// Actual implementation of functions

// Function that returns a value
int add(int a, int b) {
    int result = a + b;
    return result;  // Returns sum
}

// Void function (no return value)
void greet(string name) {
    cout << "Hello, " << name << "!\n";
    // No return statement needed for void
}

// Function with return statement
float calculateAverage(int x, int y) {
    return (x + y) / 2.0f;  // Direct return
}

// Function returning boolean
bool isEven(int number) {
    if(number % 2 == 0) {
        return true;
    } else {
        return false;
    }
}

// 3. MORE FUNCTION EXAMPLES

// Function with default return
int multiply(int a, int b) {
    return a * b;
}

// Function that calls other functions
int complexOperation(int x, int y) {
    int sum = add(x, y);
    int product = multiply(x, y);
    return sum + product;
}

// Function without parameters
void printLine() {
    cout << "------------------------\n";
}

// Function with multiple return statements
string getGrade(int marks) {
    if(marks >= 90) return "A";
    if(marks >= 75) return "B";
    if(marks >= 50) return "C";
    return "F";  // Default return
}

