#include <iostream>
#include <string>
using namespace std;

// 1. Function with parameters and return value
int multiply(int x, int y) {
    return x * y;
}

// 2. Function without return value (void)
void displayInfo(string name, int age) {
    cout << "Name: " << name << endl;
    cout << "Age: " << age << endl;
}

// 3. Function with default return
string getGrade(int marks) {
    if(marks >= 90) return "A+";
    if(marks >= 80) return "A";
    if(marks >= 70) return "B";
    if(marks >= 60) return "C";
    if(marks >= 50) return "D";
    return "F";
}

// 4. Function returning multiple values (using references)
void getMinMax(int arr[], int size, int &min, int &max) {
    min = arr[0];
    max = arr[0];

    for(int i = 1; i < size; i++) {
        if(arr[i] < min) min = arr[i];
        if(arr[i] > max) max = arr[i];
    }
}

// 5. Function with no parameters
string getCurrentTime() {
    return "10:30 AM";  // Simplified example
}

// 6. Different parameter types
float calculateBMI(float weight, float height) {
    return weight / (height * height);
}

// 7. Function returning boolean
bool isPrime(int n) {
    if(n <= 1) return false;
    for(int i = 2; i <= n/2; i++) {
        if(n % i == 0) return false;
    }
    return true;
}

// 8. Function returning pointer
int* createArray(int size) {
    int* arr = new int[size];
    for(int i = 0; i < size; i++) {
        arr[i] = i * 10;
    }
    return arr;
}

