float add(float a, float b) {
    cout << "add(float, float) called\n";
    return a + b;
}

double add(double a, double b) {
    cout << "add(double, double) called\n";
    return a + b;
}

// 3. Overloading with mixed types
string add(string a, string b) {
    cout << "add(string, string) called\n";
    return a + b;  // String concatenation
}

// 4. Overloading display functions
void display(int value) {
    cout << "Integer: " << value << endl;
}

void display(double value) {
    cout << "Double: " << value << endl;
}

void display(string value) {
    cout << "String: " << value << endl;
}

void display(int value1, int value2) {
    cout << "Two integers: " << value1 << ", " << value2 << endl;
}

// 5. Practical example: Calculator operations
int calculate(int a, int b) {
    return a + b;  // Default: addition
}

int calculate(int a, int b, char op) {
    switch(op) {
        case '+': return a + b;
        case '-': return a - b;
        case '*': return a * b;
        case '/': return a / b;
        default: return 0;
    }
}

float calculate(float a, float b, char op) {
    switch(op) {
        case '+': return a + b;
        case '-': return a - b;
        case '*': return a * b;
        case '/': return a / b;
        default: return 0;
    }
}

// 6. Constructor-like functions
void initialize(int value) {
    cout << "Initialized with int: " << value << endl;
}

void initialize(string value) {
    cout << "Initialized with string: " << value << endl;
}

void initialize(int value1, string value2) {
    cout << "Initialized with int and string: " << value1 << ", " << value2 << endl;
}

int main() {
    cout << "=== FUNCTION OVERLOADING ===\n\n";

    // Example 1: Different number of parameters
    cout << "1. Different parameter counts:\n";
    cout << "add(5, 3) = " << add(5, 3) << endl;
    cout << "add(1, 2, 3) = " << add(1, 2, 3) << endl;
    cout << "add(1, 2, 3, 4) = " << add(1, 2, 3, 4) << endl;

    // Example 2: Different parameter types
    cout << "\n2. Different parameter types:\n";
    cout << "add(5.5f, 3.2f) = " << add(5.5f, 3.2f) << endl;
    cout << "add(5.5, 3.2) = " << add(5.5, 3.2) << endl;

    // Example 3: String concatenation
    cout << "\n3. String concatenation:\n";
    cout << "add(\"Hello\", \" World\") = " << add("Hello", " World") << endl;

    // Example 4: Display function overload
    cout << "\n4. Display function overload:\n";
    display(42);
