    cout << "calculate(10, 5, '+') = " << calculate(10, 5, '+') << endl;
    cout << "calculate(10.5f, 2.5f, '*') = " << calculate(10.5f, 2.5f, '*') << endl;

    // Example 6: Initialization functions
