#include <iostream>
using namespace std;

// 1. FACTORIAL (Classic recursion example)
// n! = n * (n-1) * (n-2) * ... * 1
int factorial(int n) {
    // Base case: stopping condition
    if(n <= 1) {
        cout << "Base case: factorial(1) = 1\n";
        return 1;
    }

    // Recursive case: function calls itself
    cout << "Calculating factorial(" << n << ") = " << n << " * factorial(" << n-1 << ")\n";
    int result = n * factorial(n - 1);
    cout << "Returning factorial(" << n << ") = " << result << "\n";
    return result;
}

// 2. POWER CALCULATION
// x^n = x * x^(n-1)
double power(double x, int n) {
    // Base cases
    if(n == 0) return 1;
    if(n == 1) return x;

    // Recursive case
    return x * power(x, n - 1);
}

// 3. COUNTDOWN
void countdown(int n) {
    // Base case
    if(n < 0) {
        cout << "Blast off!\n";
        return;
    }

    // Action
    cout << n << "...\n";

    // Recursive call
    countdown(n - 1);
}

// 4. SUM OF DIGITS
int sumOfDigits(int n) {
    // Base case: single digit
    if(n < 10) return n;

    // Recursive case: last digit + sum of remaining digits
    return (n % 10) + sumOfDigits(n / 10);
}

// 5. GCD (Greatest Common Divisor) using Euclid's algorithm
int gcd(int a, int b) {
    // Base case
    if(b == 0) return a;

    // Recursive case
    return gcd(b, a % b);
}

// 6. REVERSE STRING
void reverseString(string &str, int start, int end) {
    // Base case: reached middle
    if(start >= end) return;

    // Swap characters
    swap(str[start], str[end]);

    // Recursive call
    reverseString(str, start + 1, end - 1);
}

// 7. TOWER OF HANOI
void towerOfHanoi(int n, char from, char to, char aux) {
    // Base case: only one disk
    if(n == 1) {
        cout << "Move disk 1 from " << from << " to " << to << endl;
        return;
    }

    // Move n-1 disks from 'from' to 'aux' using 'to'
    towerOfHanoi(n - 1, from, aux, to);

    // Move remaining disk from 'from' to 'to'
    cout << "Move disk " << n << " from " << from << " to " << to << endl;

    // Move n-1 disks from 'aux' to 'to' using 'from'
    towerOfHanoi(n - 1, aux, to, from);
}

// 8. BINARY SEARCH (recursive)
int binarySearch(int arr[], int low, int high, int target) {
    // Base case: element not found
    if(low > high) return -1;

    int mid = low + (high - low) / 2;

    // Base case: element found
    if(arr[mid] == target) return mid;

    // Recursive cases
    if(arr[mid] > target) {
        return binarySearch(arr, low, mid - 1, target);
    } else {
        return binarySearch(arr, mid + 1, high, target);
    }
}

int main() {
    cout << "=== RECURSION BASICS ===\n\n";

    // Example 1: Factorial
    cout << "1. Factorial of 5:\n";
    int fact = factorial(5);
    cout << "Result: " << fact << "\n\n";

    // Example 2: Power
    cout << "2. 2^5 = " << power(2, 5) << "\n\n";

    // Example 3: Countdown
    cout << "3. Countdown from 5:\n";
    countdown(5);
    cout << endl;

    // Example 4: Sum of digits
    cout << "4. Sum of digits of 12345 = " << sumOfDigits(12345) << "\n\n";

    // Example 5: GCD
    cout << "5. GCD of 48 and 18 = " << gcd(48, 18) << "\n\n";

    // Example 6: Reverse string
    string text = "Hello";
    cout << "6. Reverse string:\n";
    cout << "Original: " << text << endl;
    reverseString(text, 0, text.length() - 1);
    cout << "Reversed: " << text << "\n\n";

    // Example 7: Tower of Hanoi
    cout << "7. Tower of Hanoi (3 disks):\n";
    towerOfHanoi(3, 'A', 'C', 'B');
    cout << endl;

    // Example 8: Binary search
    cout << "8. Binary search:\n";
    int sortedArray[] = {2, 5, 8, 12, 16, 23, 38, 56, 72, 91};
    int target = 23;
    int index = binarySearch(sortedArray, 0, 9, target);
    if(index != -1) {
        cout << "Found " << target << " at index " << index << endl;
    } else {
        cout << target << " not found\n";
    }

    // Example 9: Recursion vs Iteration
    cout << "\n9. Recursion Characteristics:\n";
    cout << "- Must have base case(s)\n";
    cout << "- Must progress toward base case\n";
    cout << "- Function calls itself\n";

    return 0;
}

