#include <iostream>
using namespace std;

// Global variable - accessible everywhere
int globalCounter = 0;

void demonstrateScope(int param) {  // param is function parameter scope
    // Local variable - accessible only in this function
    int localVar = 10;

    // Static variable - persists between function calls
    static int staticVar = 0;
    staticVar++;

    // Block scope (inside {})
    {
        int blockVar = 20;  // Only accessible in this block
        cout << "Inside block: blockVar = " << blockVar << endl;
    }
    // blockVar is not accessible here

    cout << "In function: localVar = " << localVar << endl;
    cout << "In function: param = " << param << endl;
    cout << "In function: staticVar = " << staticVar << endl;
    cout << "In function: globalCounter = " << globalCounter << endl;

    globalCounter++;  // Can modify global variable
}

int main() {
    // Can access global variable
    cout << "Global in main: " << globalCounter << endl;

    // Call function multiple times
    demonstrateScope(5);
    demonstrateScope(10);
    demonstrateScope(15);

    // Check global after function calls
    cout << "Global after calls: " << globalCounter << endl;

    // localVar and param are NOT accessible here
    // cout << localVar;  // ERROR!

    return 0;
}

