#include <iostream>
using namespace std;

// ====================
// GLOBAL SCOPE
// ====================
int globalVar = 100;           // Global variable
const int MAX_SIZE = 100;      // Global constant
void demoFunction();           // Function declaration (global scope)

// ====================
// NAMESPACE SCOPE
// ====================
namespace MyNamespace {
    int nsVar = 200;           // Namespace variable
}

int main() {
    cout << "=== SCOPE AND LIFETIME ===\n\n";

    // ====================
    // LOCAL SCOPE (function)
    // ====================
    int localMain = 10;        // Local to main()

    cout << "1. Local variable in main: " << localMain << endl;
    cout << "2. Global variable: " << globalVar << endl;
    cout << "3. Namespace variable: " << MyNamespace::nsVar << "\n\n";

    // ====================
    // BLOCK SCOPE
    // ====================
    {
        int blockVar = 50;     // Only in this block
        cout << "4. Inside block: " << blockVar << endl;

        // Can access outer scope variables
        localMain = 20;        // Modify main's local
        globalVar = 150;       // Modify global
    }
    // blockVar is destroyed here
    // cout << blockVar; // ERROR! Not accessible

    cout << "5. After block, localMain: " << localMain << endl;
    cout << "6. After block, globalVar: " << globalVar << "\n\n";

    // ====================
    // FUNCTION PARAMETER SCOPE
    // ====================
    demoFunction();            // Call demo function
    demoFunction();            // Call again

    // ====================
    // LOOP SCOPE
    // ====================
    cout << "\n7. Loop scope example:\n";
    for(int i = 0; i < 3; i++) {  // i is in loop scope
        int loopLocal = i * 10;   // Created each iteration
        cout << "   Iteration " << i << ": loopLocal = " << loopLocal << endl;
    }
    // i and loopLocal are destroyed here

    // ====================
    // SAME NAME DIFFERENT SCOPES
    // ====================
    cout << "\n8. Variable shadowing:\n";
    int value = 5;
    cout << "   Outer value: " << value << endl;

    {   // Inner block
        int value = 10;        // Shadows outer value
        cout << "   Inner value: " << value << endl;
    }
    cout << "   Outer value still: " << value << "\n\n";

    // ====================
    // LIFETIME EXAMPLES
    // ====================
    cout << "9. Lifetime examples:\n";

    // Automatic (default) - destroyed when out of scope
    auto int autoVar = 30;     // Same as: int autoVar = 30;
    cout << "   Automatic variable: " << autoVar << endl;

    // Register (suggestion to store in register)
    register int regVar = 40;  // Compiler may ignore
    cout << "   Register variable: " << regVar << endl;

    // ====================
    // CONSTANTS
    // ====================
    cout << "\n10. Constants:\n";
    const int localConst = 99;
    cout << "   Local constant: " << localConst << endl;
    cout << "   Global constant: " << MAX_SIZE << endl;

    // ====================
    // SCOPE RESOLUTION
    // ====================
    cout << "\n11. Scope resolution operator (::):\n";
    int globalVar = 999;       // Shadows global globalVar
    cout << "   Local globalVar: " << globalVar << endl;
    cout << "   Global globalVar: " << ::globalVar << endl;

    return 0;
}

// ====================
// FUNCTION SCOPE
// ====================
void demoFunction() {
    // Function parameters and local variables
    static int staticCounter = 0;  // Static lifetime
    int localCounter = 0;          // Automatic lifetime

    staticCounter++;   // Persists between calls
    localCounter++;    // Reset each call

    cout << "   Static counter: " << staticCounter;
    cout << ", Local counter: " << localCounter << endl;

    // Can access global variables
    globalVar += 10;
    cout << "   GlobalVar in function: " << globalVar << endl;
}

// ====================
// CLASS SCOPE
// ====================
class MyClass {
private:
    int privateVar;    // Class scope, private

public:
    static int classVar;  // Static class variable
    void setVar(int v) {  // Member function
        privateVar = v;   // Can access privateVar
    }
    int getVar() {
        return privateVar;
    }
};

// Initialize static class variable
int MyClass::classVar = 0;

