    int arr[5] = {5, 3, 4, 1, 2};

    // Bubble sort
    for(int i=0; i<4; i++) {
        for(int j=0; j<4-i; j++) {
            if(arr[j] > arr[j+1]) {
                int temp = arr[j];
                arr[j] = arr[j+1];
                arr[j+1] = temp;
            }
        }
    }

    cout << "Sorted: ";
    for(int i=0; i<5; i++) {
        cout << arr[i] << " ";
    }

