#include <iostream>
using namespace std;

class Singleton {
private:
    static Singleton* instance;
    Singleton() {}  // private

public:
    static Singleton* getInstance() {
        if(!instance) {
            instance = new Singleton();
        }
        return instance;
    }

    void show() { cout << "Singleton" << endl; }
};

Singleton* Singleton::instance = nullptr;

int main() {
    Singleton* s = Singleton::getInstance();
    s->show();

    return 0;
}

