#include <iostream>
using namespace std;

class Singleton {
private:
    Singleton() {}

public:
    static Singleton& get() {
        static Singleton instance;
        return instance;
    }

    void log() { cout << "Singleton" << endl; }
};

int main() {
    Singleton::get().log();
    return 0;
}

