#include <iostream>
using namespace std;

class Product {
public:
    virtual void use() = 0;
};

class ConcreteProduct : public Product {
public:
    void use() override { cout << "Using product" << endl; }
};

class Factory {
public:
    static Product* create() {
        return new ConcreteProduct();
    }
};

int main() {
    Product* p = Factory::create();
    p->use();
    delete p;

    return 0;
}

