#include <iostream>
using namespace std;

class Shape {
public:
    virtual void draw() = 0;
};

class Circle : public Shape {
public:
    void draw() override { cout << "Circle" << endl; }
};

class Factory {
public:
    static Shape* createCircle() {
        return new Circle();
    }
};

int main() {
    Shape* s = Factory::createCircle();
    s->draw();
    delete s;

    return 0;
}

