#include <iostream>
#include <fstream>
using namespace std;

class FileHandler {
    ofstream file;
public:
    FileHandler(const string& filename) {
        file.open(filename);  // Acquire
    }

    ~FileHandler() {
        if(file.is_open()) {
            file.close();  // Release
        }
    }

    void write(const string& data) {
        file << data;
    }
};

int main() {
    FileHandler f("data.txt");
    f.write("Hello RAII");
    // File automatically closes
    return 0;
}

