# Function-Based Views Examples

# === 1. Basic View ===
from django.http import HttpResponse

def home(request):
    """Simple home page view"""
    return HttpResponse("<h1>Welcome to Home Page</h1>")

# === 2. View with Template ===
from django.shortcuts import render

def about(request):
    """Render template with context"""
    context = {
        'title': 'About Us',
        'company': 'Tech Solutions',
        'year': 2024
    }
    return render(request, 'about.html', context)

# === 3. View with URL Parameters ===
def user_profile(request, username):
    """Capture username from URL"""
    return HttpResponse(f"Profile of user: {username}")

# === 4. View with Query Parameters ===
def search(request):
    """Handle query parameters like ?q=django"""
    query = request.GET.get('q', '')  # Default empty string
    results = []  # Suppose we search something

    context = {
        'query': query,
        'results': results
    }
    return render(request, 'search.html', context)

# === 5. Different HTTP Methods ===
def contact(request):
    """Handle GET and POST requests"""
    if request.method == 'POST':
        # Form submitted
        name = request.POST.get('name')
        email = request.POST.get('email')
        message = request.POST.get('message')

        # Save to database or send email
        return HttpResponse(f"Thank you {name}! We'll contact you.")
    else:
        # Show form (GET request)
        return render(request, 'contact.html')

# === 6. View with Database ===
from .models import Product
from django.shortcuts import get_object_or_404

def product_detail(request, product_id):
    """Show product details from database"""
    # Get product or show 404
    product = get_object_or_404(Product, id=product_id)

    context = {
        'product': product,
        'title': product.name
    }
    return render(request, 'product_detail.html', context)

