# views.py
from django.shortcuts import render, redirect
from .forms import ContactForm, ProductForm
from django.contrib import messages

def contact_view(request):
    """Handle contact form"""
    if request.method == 'POST':
        # Form submitted
        form = ContactForm(request.POST)

        if form.is_valid():
            # Get cleaned data
            name = form.cleaned_data['name']
            email = form.cleaned_data['email']
            message = form.cleaned_data['message']

            # Process data (send email, save to DB, etc.)
            # send_contact_email(name, email, message)

            # Success message
            messages.success(request, f'Thank you {name}! We will contact you soon.')

            # Redirect to prevent form resubmission
            return redirect('contact_success')
    else:
        # GET request - show empty form
        form = ContactForm()

    return render(request, 'contact.html', {'form': form})

def create_product(request):
    """Create new product"""
    if request.method == 'POST':
        form = ProductForm(request.POST)
        if form.is_valid():
            product = form.save()  # Save to database
            messages.success(request, f'Product {product.name} created successfully!')
            return redirect('product_list')
    else:
        form = ProductForm()

    return render(request, 'products/create.html', {'form': form})

def edit_product(request, pk):
    """Edit existing product"""
    product = get_object_or_404(Product, pk=pk)

    if request.method == 'POST':
        form = ProductForm(request.POST, instance=product)
        if form.is_valid():
            form.save()
            messages.success(request, 'Product updated!')
            return redirect('product_detail', pk=product.pk)
    else:
        form = ProductForm(instance=product)

    return render(request, 'products/edit.html', {'form': form, 'product': product})

