  },
  set: function(obj, prop, value) {
    console.log(`Setting property: ${prop} = ${value}`);
    if (prop === 'age' && typeof value !== 'number') {
      throw new TypeError('Age must be a number');
    }
    obj[prop] = value;
    return true; // Required for set trap
  },
  has: function(obj, prop) {
