// Frontend (public/app.js)
// Modern JavaScript with async/await, error handling, localStorage
class WeatherApp {
  constructor() {
    this.apiKey = 'your_api_key'; // In production, use backend proxy
    this.init();
  }

  async init() {
    this.bindEvents();
    this.loadRecentSearches();
  }

  bindEvents() {
    document.getElementById('search-btn').addEventListener('click', () => this.searchWeather());
    document.getElementById('city-input').addEventListener('keypress', (e) => {
      if (e.key === 'Enter') this.searchWeather();
    });
  }

  async searchWeather() {
    const city = document.getElementById('city-input').value.trim();
    if (!city) return;

    this.showLoading();

    try {
      const weatherData = await this.fetchWeather(city);
      this.displayWeather(weatherData);
      this.saveToRecent(city);
    } catch (error) {
      this.showError(error.message);
    } finally {
      this.hideLoading();
    }
  }

  async fetchWeather(city) {
    const response = await fetch(`/api/weather?city=${encodeURIComponent(city)}`);
    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.error || 'Failed to fetch weather');
    }
    return response.json();
  }

  displayWeather(data) {
    // Update DOM with weather information
    document.getElementById('city-name').textContent = `${data.city}, ${data.country}`;
    document.getElementById('temperature').textContent = `${Math.round(data.temperature)}C`;
    document.getElementById('description').textContent = data.description;
    document.getElementById('humidity').textContent = `Humidity: ${data.humidity}%`;
    document.getElementById('wind').textContent = `Wind: ${data.windSpeed} km/h`;

    // Weather icon
    const iconUrl = `https://openweathermap.org/img/wn/${data.icon}@2x.png`;
    document.getElementById('weather-icon').src = iconUrl;

    // Show weather card
    document.getElementById('weather-card').classList.remove('hidden');
  }

  saveToRecent(city) {
    // Save to localStorage
    const recent = JSON.parse(localStorage.getItem('recentSearches') || '[]');

    // Add to beginning, remove duplicates, limit to 5
    const updated = [city, ...recent.filter(c => c !== city)].slice(0, 5);
    localStorage.setItem('recentSearches', JSON.stringify(updated));

    this.updateRecentList(updated);
  }
}
