// next.config.js
module.exports = {
  reactStrictMode: true,
  images: {
    domains: ['example.com'],
  },
}

// pages/index.js - Home page
export default function Home() {
  return <h1>Welcome Home</h1>;
}

// pages/blog/[slug].js - Dynamic route
export default function BlogPost({ post }) {
  return <article>{post.content}</article>;
}

// Button.module.css
.button {
  background: blue;
  color: white;
}

// Button.js
import styles from './Button.module.css';
export default function Button() {
  return <button className={styles.button}>Click</button>;
}

// Static Generation
export async function getStaticProps() {
  const res = await fetch('https://api.example.com/posts');
  const posts = await res.json();
  return { 
    props: { posts },
    revalidate: 60 // ISR: regenerate every 60 seconds
  };
}

// Server-side Rendering
export async function getServerSideProps(context) {
  const { req, res } = context;
  // Fresh data on every request
  return { props: { data } };
}

// pages/api/users/[id].js
export default async function handler(req, res) {
  const { id } = req.query;

  switch (req.method) {
    case 'GET':
      const user = await db.user.findUnique({ where: { id } });
      return res.status(200).json(user);

    case 'PUT':
      const updated = await db.user.update({ 
        where: { id }, 
        data: req.body 
      });
      return res.status(200).json(updated);

    case 'DELETE':
      await db.user.delete({ where: { id } });
      return res.status(204).end();
  }
}

// Using Zustand
import create from 'zustand';

const useStore = create((set) => ({
  count: 0,
  increment: () => set((state) => ({ count: state.count + 1 })),
  decrement: () => set((state) => ({ count: state.count - 1 })),
}));

// Component
function Counter() {
  const { count, increment } = useStore();
  return (
    <div>
      <span>{count}</span>
      <button onClick={increment}>+</button>
    </div>
  );
}

// pages/api/auth/[...nextauth].js
import NextAuth from 'next-auth';
import GoogleProvider from 'next-auth/providers/google';

export default NextAuth({
  providers: [
    GoogleProvider({
      clientId: process.env.GOOGLE_ID,
      clientSecret: process.env.GOOGLE_SECRET,
    }),
  ],
  callbacks: {
    async session({ session, token }) {
      session.user.id = token.sub;
      return session;
    },
  },
});

// app/layout.js - Root layout
export default function RootLayout({ children }) {
  return (
    <html lang='en'>
      <body>
        <Header />
        <main>{children}</main>
        <Footer />
      </body>
    </html>
  );
}

// app/page.js - Home page
export default function HomePage() {
  return <h1>Welcome</h1>;
}

// Server Component (default)
import db from '@/lib/db';

export default async function UserList() {
  // Direct database access in server component
  const users = await db.user.findMany();

  return (
    <ul>
      {users.map((user) => (
        <li key={user.id}>{user.name}</li>
      ))}
    </ul>
  );
}

// Client Component
'use client';
import { useState } from 'react';

export default function Counter() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <button onClick={() => setCount(count + 1)}>
        Count: {count}
      </button>
    </div>
  );
}

// app/products/page.js
export default async function ProductsPage() {
  // Direct fetch in server component
  const response = await fetch('https://api.example.com/products', {
    cache: 'no-store', // Dynamic data
  });

  const products = await response.json();

  return (
    <div>
      <h1>Products</h1>
      <Suspense fallback={<Loading />}>
        <ProductList products={products} />
      </Suspense>
    </div>
  );
}

// app/products/loading.js
export default function Loading() {
  return <div>Loading products...</div>;
}

// app/actions/user.js
'use server';
import { revalidatePath } from 'next/cache';
import db from '@/lib/db';

export async function createUser(formData) {
  'use server';

  const name = formData.get('name');
  const email = formData.get('email');

  // Validation
  if (!name || !email) {
    return { error: 'All fields required' };
  }

  // Database operation
  await db.user.create({
    data: { name, email },
  });

  // Revalidate cache
  revalidatePath('/users');

  return { success: true };
}

// app/page.js
import { createUser } from '@/app/actions/user';

export default function Home() {
  return (
    <form action={createUser}>
      <input name='name' placeholder='Name' />
      <input name='email' type='email' placeholder='Email' />
      <button type='submit'>Create User</button>
    </form>
  );
}

// Optimized Image Component
import Image from 'next/image';
import profilePic from '@/public/profile.jpg';

export default function Profile() {
  return (
    <Image
      src={profilePic}
      alt='Profile picture'
      width={500}
      height={500}
      priority={true} // Above the fold images
      placeholder='blur' // Blur while loading
      sizes='(max-width: 768px) 100vw, 50vw'
    />
  );
}

// Dynamic Import for Code Splitting
import dynamic from 'next/dynamic';
const HeavyComponent = dynamic(() => import('@/components/HeavyComponent'), {
  loading: () => <p>Loading...</p>,
  ssr: false, // Disable SSR if not needed
});

// Data fetching with revalidation
async function getData() {
  const res = await fetch('https://api.example.com/data', {
    next: { 
      revalidate: 3600, // Revalidate every hour
      tags: ['products'], // Tag for revalidation
    },
  });
  return res.json();
}

// Revalidate by tag
import { revalidateTag } from 'next/cache';

// After data mutation
revalidateTag('products');

// __tests__/Button.test.js
import { render, screen, fireEvent } from '@testing-library/react';
import Button from '@/components/Button';

describe('Button Component', () => {
  test('renders button with text', () => {
    render(<Button>Click me</Button>);
    expect(screen.getByText('Click me')).toBeInTheDocument();
  });

  test('calls onClick when clicked', () => {
    const handleClick = jest.fn();
    render(<Button onClick={handleClick}>Click</Button>);

    fireEvent.click(screen.getByText('Click'));
    expect(handleClick).toHaveBeenCalledTimes(1);
  });
});

