// Test readable stream
function testReadableStream() {
  const readable = new Readable({
    read(size) {
      this.push('data');
      this.push(null);
    }
  });

  let data = '';
  readable.on('data', chunk => {
    data += chunk.toString();
  });

  return new Promise((resolve) => {
    readable.on('end', () => {
      assert.strictEqual(data, 'data');
      resolve();
    });
  });
