# Simple process manager script
const { spawn } = require('child_process');
let child = null;

function start() {
  child = spawn('node', ['app.js'], {
    stdio: 'inherit',
    detached: false
  });

  child.on('exit', (code) => {
    console.log(`Process exited with code ${code}`);
    if (code !== 0) {
      console.log('Restarting...');
      setTimeout(start, 1000);
    }
  });
}

start();

const cluster = require('cluster');

if (cluster.isPrimary) {
  const workers = [];

  // Fork workers
  for (let i = 0; i < 4; i++) {
    const worker = cluster.fork();
    workers.push(worker);
  }

  // Health check interval
  setInterval(() => {
    workers.forEach(worker => {
      worker.send({ type: 'health_check' });
    });
  }, 30000);

  // Monitor responses
  cluster.on('message', (worker, message) => {
    if (message.type === 'health_status') {
      console.log(`Worker ${worker.process.pid}: ${message.status}`);
    }
    if (message.type === 'metrics') {
      console.log(`Worker ${worker.process.pid} metrics:`, message.data);
    }
  });
} else {
  // Worker listens for health checks
  process.on('message', (message) => {
    if (message.type === 'health_check') {
      // Send health status
      process.send({
        type: 'health_status',
        status: 'healthy',
        memory: process.memoryUsage()
      });
    }
  });

  // Periodically send metrics
  setInterval(() => {
    process.send({
      type: 'metrics',
      data: {
        memory: process.memoryUsage(),
        uptime: process.uptime()
      }
    });
  }, 60000);
}
