// wp-config.php database settings
define('DB_NAME', 'wordpress_db');
define('DB_USER', 'root');
define('DB_PASSWORD', 'password');
define('DB_HOST', 'localhost');
define('DB_CHARSET', 'utf8mb4');

// functions.php - Register Custom Post Type
function register_portfolio_cpt() {
    register_post_type('portfolio',
        array(
            'labels' => array(
                'name' => __('Portfolio'),
                'singular_name' => __('Portfolio Item')
            ),
            'public' => true,
            'has_archive' => true,
            'supports' => array('title', 'editor', 'thumbnail'),
            'menu_icon' => 'dashicons-portfolio',
        )
    );
}
add_action('init', 'register_portfolio_cpt');

// Register Sidebar
register_sidebar(array(
    'name' => 'Main Sidebar',
    'id' => 'main-sidebar',
    'before_widget' => '<div class="widget">',
    'after_widget' => '</div>',
));

// Theme Customizer Setting
function mytheme_customize_register($wp_customize) {
    $wp_customize->add_setting('header_color', array(
        'default' => '#ffffff',
        'transport' => 'refresh',
    ));

    $wp_customize->add_control(new WP_Customize_Color_Control(
        $wp_customize,
        'header_color',
        array(
            'label' => __('Header Color'),
            'section' => 'colors',
        )
    ));
}
add_action('customize_register', 'mytheme_customize_register');

// Action Hook Example
function myplugin_custom_action() {
    echo '<p>Action executed!</p>';
}
add_action('wp_footer', 'myplugin_custom_action');

// Filter Hook Example
function myplugin_modify_title($title) {
    return $title . ' - My Site';
}
add_filter('the_title', 'myplugin_modify_title');

// Custom Hook Creation
do_action('myplugin_custom_hook'); // Trigger action
apply_filters('myplugin_custom_filter', $data); // Apply filter

// Create custom table on plugin activation
function myplugin_create_table() {
    global $wpdb;

    $table_name = $wpdb->prefix . 'myplugin_data';
    $charset_collate = $wpdb->get_charset_collate();

    $sql = "CREATE TABLE $table_name (
        id mediumint(9) NOT NULL AUTO_INCREMENT,
        name varchar(100) NOT NULL,
        email varchar(100) NOT NULL,
        created_at datetime DEFAULT CURRENT_TIMESTAMP,
        PRIMARY KEY (id)
    ) $charset_collate;";

    require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
    dbDelta($sql);
}
register_activation_hook(__FILE__, 'myplugin_create_table');

// Secure form processing
function myplugin_process_form() {
    // Check nonce
    if (!isset($_POST['myplugin_nonce']) || 
        !wp_verify_nonce($_POST['myplugin_nonce'], 'myplugin_action')) {
        return;
    }

    // Check capabilities
    if (!current_user_can('manage_options')) {
        wp_die('Unauthorized access');
    }

    // Sanitize input
    $name = sanitize_text_field($_POST['name']);
    $email = sanitize_email($_POST['email']);

    // Validate
    if (!is_email($email)) {
        wp_die('Invalid email');
    }

    // Safe database operation
    global $wpdb;
    $wpdb->insert(
        $wpdb->prefix . 'myplugin_data',
        array('name' => $name, 'email' => $email),
        array('%s', '%s')
    );
}
add_action('admin_post_myplugin_form', 'myplugin_process_form');

// Custom product type registration
function register_custom_product_type() {
    class WC_Product_Custom extends WC_Product {
        public function __construct($product) {
            parent::__construct($product);
            $this->product_type = 'custom';
        }
    }
}
add_action('init', 'register_custom_product_type');

// Add to product type selector
function add_custom_product_type($types) {
    $types['custom'] = __('Custom Product');
    return $types;
}
add_filter('product_type_selector', 'add_custom_product_type');

// Register custom REST API route
add_action('rest_api_init', function() {
    register_rest_route('myplugin/v1', '/data/(?P<id>\d+)', array(
        'methods' => 'GET',
        'callback' => 'myplugin_get_data',
        'permission_callback' => function() {
            return current_user_can('read');
        },
        'args' => array(
            'id' => array(
                'validate_callback' => function($param) {
                    return is_numeric($param);
                }
            ),
        ),
    ));
});

function myplugin_get_data($request) {
    $id = $request['id'];
    // Get data logic
    return rest_ensure_response(array('id' => $id, 'data' => 'value'));
}

// Object caching example
$data = wp_cache_get('my_data');
if (false === $data) {
    $data = expensive_database_query();
    wp_cache_set('my_data', $data, '', 3600); // Cache for 1 hour
}

// Transients for persistent caching
$result = get_transient('api_response');
if (false === $result) {
    $result = wp_remote_get('https://api.example.com/data');
    set_transient('api_response', $result, HOUR_IN_SECONDS);
}

// Redis Object Cache setup
// wp-config.php
define('WP_REDIS_HOST', '127.0.0.1');
define('WP_REDIS_PORT', 6379);
define('WP_REDIS_TIMEOUT', 1);
define('WP_REDIS_READ_TIMEOUT', 1);

// Cache invalidation on post save
function clear_post_cache($post_id) {
    // Clear specific post cache
    wp_cache_delete('post_' . $post_id, 'posts');

    // Clear related transients
    delete_transient('recent_posts');
    delete_transient('post_count');

    // Clear page cache if using plugin
    if (function_exists('w3tc_pgcache_flush')) {
        w3tc_pgcache_flush();
    }
}
add_action('save_post', 'clear_post_cache');

// Disable file editor in wp-config.php
define('DISALLOW_FILE_EDIT', true);

// Move wp-config.php one level above
// Move to directory above WordPress root

// Security headers
function add_security_headers() {
    header('X-Content-Type-Options: nosniff');
    header('X-Frame-Options: SAMEORIGIN');
    header('X-XSS-Protection: 1; mode=block');
    header('Referrer-Policy: strict-origin-when-cross-origin');
}
add_action('send_headers', 'add_security_headers');

// Hide WordPress version
function remove_version_info() {
    return '';
}
add_filter('the_generator', 'remove_version_info');

// Enable Multisite in wp-config.php
define('WP_ALLOW_MULTISITE', true);

